import axios from "axios"
import { useEffect, useState } from "react"
import { Link, Outlet } from "react-router-dom"
import 'boxicons'
import './Exercices.css'
import NavBar from "../NavBar/NavBar"
import swal from 'sweetalert'


const Exercices = () =>{

    const [exercicess, setExercicess] = useState([])
    const token = localStorage.getItem('firsLogin')
    // console.log(token)

    useEffect(()=>{
        axios.get("http://localhost:5000/api/exercicesUser", {headers:{"Authorization": token}} ).then((response) =>{
            // console.log(response)
            setExercicess(response.data.exercices)
            // console.log(exercicess)
        })
    }, [])
    // console.log(exercicess)

    const showAlert = (IDExercice) =>{
        console.log(IDExercice)
        localStorage.setItem('exercice', IDExercice)
        swal({
            text: "¿Estás seguro que quieres eliminar el WOD?",
            icon: "warning",
            buttons: ["No", "Si"]
        }).then(respuesta  =>{
            let IDEXERCICE = localStorage.getItem("exercice")
            if(respuesta){
                axios.delete(`http://localhost:5000/api/deleteExercice/${IDEXERCICE}`, {headers:{"Authorization": token}})
                swal({text: "El Ejercicio se a eliminado correctamente", icon: "success", timer: "2000"})
                localStorage.removeItem("wod")
            }   
            })
    }


    return(
        <div>
            <NavBar />
            <h2 className="subtitle">Listado de ejercicios</h2>
            <Link className="container_addExercice" to="addExercice">
            <box-icon name='plus-circle' size='lg' color='grey'></box-icon>
            </Link>
            <Outlet />
            <br />
            {
            exercicess.map((EXERCICES)=>{
                    return(
                        <div key={EXERCICES._id}>
                        <Link className="container_exercices" to={`/marks/${EXERCICES._id}`}>
                        <button className="exercices_name">{EXERCICES.nameExercice}</button></Link>
                        {/* <Link className="button_user_delete" to={`/deleteExercice/${EXERCICES._id}`}> ❌ </Link> */}
                        <div onClick={()=>{showAlert(EXERCICES._id)}}>❌</div>
                        </div>

                    )
                })
            }

            <Link to="/home">
                <div className="container_back_button">
                <box-icon name='left-arrow-circle' size='lg' color='grey' className="back_button"></box-icon>
                </div>
            </Link>

        </div>
    )
}

export default Exercices
