import axios from 'axios'
import { useEffect, useState } from 'react'
import './Profile.css'

const ProfilePhoto = () =>{

    const token = localStorage.getItem("firsLogin")
    const [imagen, setImagen] = useState(null)

    useEffect(()=>{
        axios.get("http://localhost:5000/api/profile", {headers:{"Authorization": token}}).then((response) =>{

            setImagen(response.data.user.imagen.url)
        })
    }, [])


    return(
        <div>
            <div className="container">
                <h2 className="subtitle">Perfil - Cambiar Foto</h2>
                <div className="container_photo">
                    <img className="profile_photo" src={imagen}></img>
                </div>
                <div className="container_buttons">
                <button className="button_submit">Examinar</button>
                <button className="button_submit">Aceptar</button>
                </div>
                <button className="button_delete_photo">Eliminar foto</button>
            </div>


        </div>
    )
}

export default ProfilePhoto
