import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { Link } from "react-router-dom";


const Marks = () => {
    const { exerciceID } = useParams();
    const token = localStorage.getItem("firsLogin")
    const [markList, setMarkList] = useState(null);

    useEffect(() => {
        const getMark = async () => {
            const response = await axios.get(`http://localhost:5000/api/marksUserList/${exerciceID}`, {headers:{"Authorization": token}});
            // axios.get(`http://localhost:5000/api/marksUserList/${exerciceID}`, {headers:{"Authorization": token}}) .then((response)=>{
                console.log("response.data.marks", response.data.marks)
                setMarkList(response.data.marks);
                console.log("markList", markList)
            // })


        };
        getMark();
    }, )

    const loading = () =>{
        return "loading"
    }

    const screen = () =>{

    return (
        <div>

            <table className="marks_table">
            <thead className="header_marks">
                <th>Fecha</th>
                <th>Reps</th>
                <th>Peso</th>
                <th>Comentario</th>
            </thead>
            <tbody>
                {
                    markList.map((marksExer)=>{
                        return(
                            <tr key={marksExer._id} className="rowMark">
                                <td>{marksExer.date}</td>
                                <td>{marksExer.reps}</td>
                                <td>{marksExer.weight}</td>
                                <td>{marksExer.comment}</td>
                            </tr>
                        )
                    })
                }
            </tbody>
            </table>

        </div>
    )
    }

    return(
        <div>
            {
                markList ? screen() : loading()
            }
        </div>
    )

}
export default Marks;




