import { Link } from "react-router-dom"
import 'boxicons'
import { useState } from "react"
import axios from "axios"
import NavBar from "../NavBar/NavBar"


const WodCreate = () =>{

    const token = localStorage.getItem("firsLogin")

    const [info, setInfo] = useState({
        type: "",
        name: "",
        time: "",
        description: ""
    })
    const [errorMessage, setErrorMessage] = useState(null)
    const [successMessage, setSuccessMessage] = useState(null)

    const onChangeInput = (e) =>{
        const {name, value} = e.target
        setInfo({...info, [name]: value})
    }
    // console.log(info)
    const submitCreateWod = async e =>{
        e.preventDefault()

        try {
            const response = await axios.post("http://localhost:5000/api/createWod", {...info}, {headers:{"Authorization": token}})
            setSuccessMessage(response.data.message)
        } catch (error) {
            setErrorMessage(error.response.data.message)
        }

    }


    return(
        <div>
            <NavBar />
            <form onSubmit={submitCreateWod}> 
            <h2 className="subtitle">Crea un WOD</h2>
            <div className="containerRegister">
            <div className="form-floating mb-3 container_inputReg">
                <input type="text" name="type" value={info.type} onChange={onChangeInput} className="form-control" id="floatingTypeWod" placeholder="Tipo de WOD" />
                <label htmlFor="floatingTypeWod">AMRAP, EMOM o For Time</label>
            </div>
            {/* <p>Tipo de WOD </p>    
            <input type="text" name="type" value={info.type} onChange={onChangeInput} placeholder="For Time AMRAP  EMOM"></input> */}
            <div className="form-floating mb-3 container_inputReg">
                <input type="text" name="name" value={info.name} onChange={onChangeInput} className="form-control" id="floatingNameWod" placeholder="Nombre" />
                <label htmlFor="floatingNameWod">Nombre</label>
            </div>
            {/* <p>Nombre</p>    
            <input type="text" name="name" value={info.surname} onChange={onChangeInput}></input> */}
            <div className="form-floating mb-3 container_inputReg">
                <input type="text" name="time" value={info.time} onChange={onChangeInput} className="form-control" id="floatingNameTime" placeholder="Tiempo" />
                <label htmlFor="floatingNameTime">Tiempo</label>
            </div>
            {/* <p>Tiempo</p>    
            <input type="text" name="time" value={info.email} onChange={onChangeInput}></input> */}
            <div className="form-floating mb-3 container_inputReg">
                <textarea type="text" name="description" value={info.description} onChange={onChangeInput} className="form-control texArea_wod" id="floatingDescription" placeholder="Descripción" />
                <label htmlFor="floatingDescription">Descripción</label>
            </div>
            {/* <p>Descripción</p>    
            <input type="text" name="description" value={info.password} onChange={onChangeInput}></input>
            <textarea name="description" value={info.password} onChange={onChangeInput} className="form-control" aria-label="With textarea"></textarea> */}
            </div>
            <div style={{display: successMessage ? "block": "none"}}>{successMessage}</div>
            <div style={{display: errorMessage ? "block": "none"}}>{errorMessage}</div>
            <button type="submit" className="button_submit" >Crear</button>

        </form>

            <Link to="/home">
                <div className="container_back_button">
                <box-icon name='left-arrow-circle' size='lg' color='grey' className="back_button"></box-icon>
                </div>
            </Link>
        </div>
    )
}

export default WodCreate
