import axios from "axios"
import { useEffect, useState } from "react"
import { Link, useParams } from "react-router-dom"


const TimeTableAdd = () =>{
    const {classesID} = useParams()
    const token = localStorage.getItem("firsLogin")
    const [timeTables, setTimeTables] = useState(null)

    useEffect (()=>{
        const getTimeTable = async ()=>{

            const response = await axios.get(`http://localhost:5000/api/classesList/${classesID}`, {headers:{"Authorization": token}})
            console.log(response.data.timeTable)
            setTimeTables(response.data.timeTable)
            // console.log(timeTables)
        }

        getTimeTable()


    },[])

    const loading = ()=>{
        return <div></div>
    }

    const screen = () =>{
    return(
    <div>
        <h2 className="subtitle">Listado de horarios</h2>
        {
            timeTables.map((timetable)=>{
                return(
                <div key={timetable._id}>
                    <tr>
                        <td>{timetable.time}</td>
                        <td>{timetable.nPeople}</td>
                    </tr>
                </div>
                )
            })
        }
        <Link to="/timetables">
            <div className="container_back_button">
                <box-icon name='left-arrow-circle' size='lg' color='grey' className="back_button"></box-icon>
            </div>
        </Link>
    </div>
    )
}
return(
    <div>
        {
            timeTables ? screen() : loading()
        }
    </div>
)
}

export default TimeTableAdd
