import axios from "axios"
import { useState } from "react"
import { useParams } from "react-router-dom"


const TimeTableAdd = () =>{

    const {classesID2} = useParams()
    const token = localStorage.getItem()
    const [info, setInfo] = useState({
        time: "",
        nPeople: "",
        date: classesID2
    })
    const [successMessage, setSuccessMessage] = useState(null)
    const [errorMessage, setErrorMessage] = useState(null)

    const onChangeInput = (e) =>{
        const {name, value} = e.target
        setInfo({...info, [name]: value})
    }
    console.log(info)

    const addTimeSubmit = async e  =>{
        e.preventDefault()
        try {
            const response = await axios.post(`http://localhost:5000/api/createTime/${classesID2}`, {headers:{"Authorization": token}})
            setSuccessMessage(response.data.message)
        } catch (error) {
            setErrorMessage(error.response,data.message)
        }

    }

    return(
        <div>
            <h4 >Añade un nuevo horario</h4>
            <form onSubmit={addTimeSubmit} className="formNewTimetable">
                <input name="time" className="input_time" onChange={onChangeInput} value={info.time} placeholder="Hora"></input>
                <input name="nPeople" className="input_time" onChange={onChangeInput} value={info.nPeople} placeholder="Nº de Personas"></input>
                <div style={{display: successMessage ? "block": "none"}}>{successMessage}</div>
                <div style={{display: errorMessage ? "block": "none"}}>{errorMessage}</div>
                <button type="submit" className="button_submit">Añadir</button>
            </form>

        </div>
    )
}

export default TimeTableAdd
