import { useState } from "react"
import axios from 'axios'
import { Link } from "react-router-dom"
import './Register.css'
import NavBar from "../NavBar/NavBar"


const Register = () => {

    const token = localStorage.getItem("firsLogin")
    const [info, setInfo] = useState({
        name: "",
        surname: "",
        email: "",
        password: "",
        imagen: {"public_id": "", "url": "https://res.cloudinary.com/dzenpc7wi/image/upload/v1642780148/crossfitAPP/auxt7ab5smg7879zbq4f.webp"}
    })

const [errorMessage, setErrorMessage] = useState(null)
const [successMessage, setSuccessMessage] = useState(null)



const onChangeInput = (e) =>{
    const {name, value} = e.target
    setInfo({...info, [name]: value})

}
console.log(info)



//e.preventdefault No refresca la página hasta que envíes la información
const registerSubmit = async e => {
    e.preventDefault()         
    try {

        const response = await axios.post("http://localhost:5000/api/register", {...info}, {headers:{"Authorization": token}})
        // localStorage.setItem("firstLogin", true)    guarda token para saber que estás logueado
        // localhost.removeItem() para desloguearte
        setSuccessMessage(response.data.message)
        // console.log(response)


    } catch (error) {
        setErrorMessage(error.response.data.message)
        // console.log(error.response)
    }

}
    return(
    <div>
        <NavBar />
        <form onSubmit={registerSubmit}> 
        <h2 className="subtitle">Registrar un usuario</h2>
        <div className="containerRegister">
            <div className="form-floating mb-3 container_inputReg">
                <input type="text" name="name" value={info.name} onChange={onChangeInput} className="form-control" id="floatingName" placeholder="Nombre" />
                <label htmlFor="floatingName">Nombre</label>
            </div>
            <div className="form-floating mb-3 container_inputReg">
                <input type="text" name="surname" value={info.surname} onChange={onChangeInput} className="form-control" id="floatingSurname" placeholder="Apellido" />
                <label htmlFor="floatingSurname">Apellido</label>
            </div>
            <div className="form-floating mb-3 container_inputReg">
                <input type="email" name="email" value={info.email} onChange={onChangeInput} className="form-control" id="floatingEmail" placeholder="email@example.com" />
                <label htmlFor="floatingEmail">Email</label>
            </div>
            <div className="form-floating mb-3 container_inputReg">
                <input type="password" name="password" value={info.password} onChange={onChangeInput} className="form-control" id="floatingPassword" placeholder="Password" />
                <label htmlFor="floatingPassword">Contraseña</label>
            </div>
        </div>
        <div style={{display: successMessage ? "block": "none"}}>{successMessage}</div>
        <div style={{display: errorMessage ? "block": "none"}}>{errorMessage}</div>
        <button type="submit" className="button_submit">Registrar</button>

        </form>
        <Link to="/home">
                <div className="container_back_button">
                {/* <box-icon name='left-arrow-circle' size='lg' color='grey' className="back_button"></box-icon> */}
                <i className="far fa-arrow-alt-circle-left buttonBack"></i>
                </div>
            </Link>
    </div>
    )
}

export default Register
