import { Link } from "react-router-dom"
import 'boxicons'
import { useState } from "react"
import axios from "axios"


const WodCreate = () =>{

    const token = localStorage.getItem("firsLogin")

    const [info, setInfo] = useState({
        type: "",
        name: "",
        time: "",
        description: ""
    })
    const [errorMessage, setErrorMessage] = useState(null)
    const [successMessage, setSuccessMessage] = useState(null)

    const onChangeInput = (e) =>{
        const {name, value} = e.target
        setInfo({...info, [name]: value})
    }
    console.log(info)
    const submitCreateWod = async e =>{
        e.preventDefault()

        try {
            const response = await axios.post("http://localhost:5000/api/createWod", {...info}, {headers:{"Authorization": token}})
            setSuccessMessage(response.data.message)
        } catch (error) {
            setErrorMessage(error.response.data.message)
        }

    }


    return(
        <div>
            <form onSubmit={submitCreateWod}> 
            <h2 className="subtitle">Registrar un usuario</h2>
            <div className="containerRegister">
            <p>Tipo (For Time, AMRAP or EMOM) </p>    
            <input type="text" name="type" value={info.value} onChange={onChangeInput}></input>
            <p>Nombre</p>    
            <input type="text" name="name" value={info.surname} onChange={onChangeInput}></input>
            <p>Tiempo</p>    
            <input type="text" name="time" value={info.email} onChange={onChangeInput}></input>
            <p>Descripci√≥n</p>    
            {/* <input type="text" name="description" value={info.password} onChange={onChangeInput}></input> */}
            <textarea name="description" value={info.password} onChange={onChangeInput} className="form-control" aria-label="With textarea"></textarea>
            </div>
            <div style={{display: successMessage ? "block": "none"}}>{successMessage}</div>
            <div style={{display: errorMessage ? "block": "none"}}>{errorMessage}</div>
            <button type="submit" >Crear</button>

        </form>

            <Link to="/home">
                <div className="container_back_button">
                <box-icon name='left-arrow-circle' size='lg' color='grey' className="back_button"></box-icon>
                </div>
            </Link>
        </div>
    )
}

export default WodCreate
