import axios from 'axios'
import { useState } from 'react'
import './Login.css'


const Login = () =>{
    const [info, setInfo] = useState({
        email: "",
        password: ""
    })

const [errorMessage , setErrorMessage] = useState(null)
const [successMessage, setSuccessMessage] = useState(null)

const onChangeInput = (e) =>{
    const {name , value} = e.target
    setInfo({...info, [name]: value})
}
console.log(info)

const loginSubmit = async e =>{

    e.preventDefault()
    try {
        const response = await axios.post ("http://localhost:5000/api/login", {...info})

        setSuccessMessage(response.data.message)
    } catch (error) {
        setErrorMessage(error.response.data.message)
    }
}

    return(
        <div> 
            <form>
            <h2 className="tittleLogin">¡¡Bienvenido!!</h2>
            <h3>¡Introduce tus datos de acceso!</h3>
            <input type="text" placeholder="Email" name="email" onChange={onChangeInput}></input> <br />
            <input type="text" placeholder="Contraseña" name="password" onChange={onChangeInput}></input> <br />
            <button>Enviar</button>
            </form>
        </div>
    )

}
export default Login
