import axios from "axios"
import 'boxicons'
import { useEffect, useState } from "react"
import { Link, useParams } from "react-router-dom"
import NavBar from "../NavBar/NavBar"
import './Booking.css'

const Booking = () =>{
    const token = localStorage.getItem("firsLogin")
    const {classID} = useParams()
    const [dateWod, setDateWod] = useState(null)
    const [timeTable, setTimeTable] = useState(null)
    const [users, setUsers] = useState(null)
    const [show, setShow]= useState(false)

    useEffect(() =>{
        axios.get(`http://localhost:5000/api/bookingList/${classID}`, {headers:{"Authorization": token}}).then((response)=>{
            console.log("response", response)
            setTimeTable(response.data.horario)
            setUsers(response.data.usuarios)
            setDateWod(response.data.date)
        })


    },[])

    console.log("users", users)

//     const filterUser = ()=>{


//     const usersClass = users.filter((USERS)=>{
//         if (USERS.timeTable == )
//     })
// }

    const loading = () =>{
        return loading
    }

    const screen = ()=>{
        let correctDate = ""
                let date1= new Date(dateWod.date)
                let year = date1.getFullYear();
                let month = date1.getMonth() + 1;
                let dt = date1.getDate();
                if (dt < 10) {
                    dt = '0' + dt;
                }
                if (month < 10) {
                    month = '0' + month;
                }
                correctDate = dt + "/" + month + "/" + year
    return(
        <div>
            <NavBar />
            <h2 className="subtitle">Reserva tu clase</h2>

            <div className="time_booking">
                <h1 className="">{correctDate}</h1>
            </div>
            {
                show ? <div className="wodDay_booking">{dateWod.wodDay}</div>: null
            }
            <button onClick={()=> setShow(!show)}>WOD del día</button>
            {
                timeTable.map((time)=>{
                    return(
                        <div key={time._id}>
                            <p>{time.time}</p>
                            <button >Reservar</button>
                        </div>
                    )
                })
            }
            <Link to="/listclasses">
            <div className="container_back_button">
                <box-icon name='left-arrow-circle' size='lg' color='grey' className="back_button"></box-icon>
            </div>
            </Link>
        </div>
    )
}
return(
    <div>
        {
            dateWod ? screen() : loading()
        }
    </div>
)
}

export default Booking
